import React, { Component } from "react";

import { Menu } from "antd";
import "../Sidebar/Sidebar.css";

import { HomeOutlined, SearchOutlined, UserOutlined } from "@ant-design/icons";

import MovieIcon from "../Icons/MovieIcon";

import history from "../../history";

class Sidebar extends Component {
  state = {
    collapsed: true,
  };

  handleClickMenuItem = (link) => {
      history.push(link)
  }

  render() {
    return (
      <div
        style={{ width: 256 }}
      >
        <Menu
          defaultSelectedKeys={["1"]}
          defaultOpenKeys={["sub1"]}
          mode="inline"
          theme="dark"
          inlineCollapsed={this.state.collapsed}
          inlineIndent='50'
          style={{
            height: "95vh",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
          }}
        >
          <Menu.Item
            className="menu-item"
            key="1"
            icon={<HomeOutlined />}
            onClick={(e) => this.handleClickMenuItem('/')}
          >
            Home
          </Menu.Item>

          <Menu.Item
            className="menu-item"
            key="2"
            icon={<MovieIcon style={{color: '#ffffff'}} />}
            onClick={(e) => this.handleClickMenuItem('/movie')}
          >
            Movie
          </Menu.Item>

          <Menu.Item
            className="menu-item"
            key="3"
            icon={<SearchOutlined />}
            onClick={(e) => this.handleClickMenuItem('/search')}
          >
            Search
          </Menu.Item>
          <Menu.Item
            className="menu-item"
            key="4"
            icon={<UserOutlined />}
            onClick={e => this.handleClickMenuItem('/profile')}
          >
            Profile
          </Menu.Item>
        </Menu>
      </div>
    );
  }
}

export default Sidebar;
